//Recup de la liste <ul> html depuis son id
let resultatParent = document.getElementById("resultats");
let tousProduits = document.getElementById("tousProduits");


function afficherProduits(){
    tableauProduits.forEach(produitItem => {
        let produitItems = document.createElement("div")
        produitItems.innerHTML =
            `
            <div>${produitItem.nomProduit}</div>
            <div>${produitItem.prixProduit}</div>
            <div>
                <img src="${produitItem.image}" width="25%">
            </div>
            <div style="color: #BA4C63">${produitItem.categorie}</div>
            
            `
        tousProduits.appendChild(produitItems)
    });
}


//Fonction afficher
function afficherProduitsCategories() {
    //Recup de tous les input HTML
    let checkbox = document.querySelectorAll("input[type='checkbox']");
    //boucle sur tous les <input type="checkbox">
    for(box of checkbox){
        //Quand etat change = coché ou non coché
        box.onchange = function (event) {
            //Recup de la valeur de input
            let checkBoxValue = event.target.value
            //Recup de l'etat de la check box
            let isChecked = event.target.checked
            //Si c coché
            if(isChecked){
                //Css = display block
                //resultatParent.style.display = "block"
                //Si la valeur de input est === a loisir
                if(checkBoxValue === "loisir"){
                    //Debug
                    console.log("c bon")
                    //Parcours du tableau de produit
                    tableauProduits.forEach(produit => {
                        //Si dans le tableau la valeur de la clé categorie === loisir
                        if(`${produit.categorie}` === "loisir"){
                            //On affiche les produits qui on la cle + la valeur loisir
                            let produitElement = document.createElement("p")
                            produitElement.id = `p-${produit.id}`
                            produitElement.innerHTML =
                                `
                                <p>${produit.nomProduit}</p>
                                <p>${produit.prixProduit} €</p>
                                <p>
                                    <img src="${produit.image}" width="50%">
                                </p>
                                <p style="color: #BA4C63">Catégories : ${produit.categorie}</p>
                                
                                `
                            //Ajout des <p> a la div parente resultat
                            resultatParent.appendChild(produitElement)
                        }
                    })
                }else if(checkBoxValue === "meuble"){
                    tableauProduits.forEach(produit => {
                        if(`${produit.categorie}` === "meuble"){
                            let produitElement = document.createElement("p")
                            produitElement.id = `p-${produit.id}`
                            produitElement.innerHTML =
                                `
                                <p>${produit.nomProduit}</p>
                                <p>${produit.prixProduit} €</p>
                                <p>
                                    <img src="${produit.image}" width="50%">
                                </p>
                                <p style="color: #BA4C63">Categories : ${produit.categorie}</p>
                                
                                `

                            resultatParent.appendChild(produitElement)
                        }
                    })
                }else if(checkBoxValue === "informatique"){
                    tableauProduits.forEach(produit => {
                        if(`${produit.categorie}` === "informatique"){
                            let produitElement = document.createElement("p")
                            produitElement.id = `p-${produit.id}`
                            produitElement.innerHTML =
                                `
                                <p>${produit.nomProduit}</p>
                                <p>${produit.prixProduit} €</p>
                                <p>
                                    <img src="${produit.image}" width="50%">
                                </p>
                                <p style="color: #BA4C63">Catégories : ${produit.categorie}</p>
                                
                                `
                            resultatParent.appendChild(produitElement)
                        }
                    })
                }else{
                    //On creer les paragraphe avec leurs id vide
                    tableauProduits.forEach(produit => {
                        let produitElement = document.createElement("p")
                        produitElement.id = `p-${produit.id}`
                        produitElement.innerHTML = ""
                        resultatParent.appendChild(produitElement)
                    })
                }
            }else{
                //On recup les paragraphe vide si rien n'est coché et dans une boucle on cache les elements
                let p = document.querySelectorAll("p");
                for(paragraphe of p){
                    //paragraphe.style.display = "none"
                    paragraphe.remove()
                }

            }
        }
    }
}

afficherProduitsCategories()

afficherProduits();



